mapper.select:
select d.*,c.name as categoryName from dish as d left join category as c on d.category_id = c.id

    category_id = #{categoryId}
    and d.name like concat('%',#{name},'%')
    and d.status = #{status}

order by create_time desc

mapper.update:
update dish
category_id = #{categoryId}, description = #{description}, image = #{image}, name = #{name}, price = #{price}, status = #{status}, update_time=#{updateTime}, update_user=#{updateUser},
where id = #{id}
